
public class CharWrapLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1='k';
		char ch2='d';
		char ch3='P';
		
		boolean b1= Character.isLowerCase(ch1);
		boolean b2= Character.isLowerCase(ch2);
		boolean b3= Character.isLowerCase(ch3);
		
		System.out.println(ch1+ " is LowerCase:?? "+b1);
		System.out.println(ch2+ " is LowerCase:?? "+b2);
		System.out.println(ch3+ " is LowerCase:?? "+b3);
	}

}
