
public class CharWrapToLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1='A';
		char ch2='d';
		char ch3='M';
		
		char ch11= Character.toLowerCase(ch1);
		char ch22= Character.toLowerCase(ch2);
		char ch33= Character.toLowerCase(ch3);
		
		System.out.println(ch1+ " is LowerCase:?? "+ch11);
		System.out.println(ch2+ " is LowerCase:?? "+ch22);
		System.out.println(ch3+ " is LowerCase:?? "+ch33);

	}

}
