
public class CharWrapToUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1='k';
		char ch2='d';
		char ch3='P';
		
		char ch11= Character.toUpperCase(ch1);
		char ch22= Character.toUpperCase(ch2);
		char ch33= Character.toUpperCase(ch3);
		
		System.out.println(ch1+ " is UpperCase:?? "+ch11);
		System.out.println(ch2+ " is UpperCase:?? "+ch22);
		System.out.println(ch3+ " is UpperCase:?? "+ch33);

	}

}
