
public class CharWrapUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1='k';
		char ch2='d';
		char ch3='P';
		
		boolean b1= Character.isUpperCase(ch1);
		boolean b2= Character.isUpperCase(ch2);
		boolean b3= Character.isUpperCase(ch3);
		
		System.out.println(ch1+ " is UpperCase:?? "+b1);
		System.out.println(ch2+ " is UpperCase:?? "+b2);
		System.out.println(ch3+ " is UpperCase:?? "+b3);
	}

}
